import type { NextApiRequest, NextApiResponse } from 'next';
import path from 'path';
import fs from 'fs';

// Mock image data - in production, these would be real AI-generated images
const mockImageData = {
  'robot-1.jpg': {
    width: 512,
    height: 512,
    contentType: 'image/jpeg',
    // Base64 encoded 1x1 pixel placeholder - replace with actual image data
    data: ''
  },
  'robot-2.jpg': {
    width: 512,
    height: 512,
    contentType: 'image/jpeg',
    data: ''
  },
  'cosmic-1.jpg': {
    width: 512,
    height: 512,
    contentType: 'image/jpeg',
    data: ''
  },
  'abstract-1.jpg': {
    width: 512,
    height: 512,
    contentType: 'image/jpeg',
    data: ''
  },
  'neon-1.jpg': {
    width: 512,
    height: 512,
    contentType: 'image/jpeg',
    data: ''
  }
};

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  if (req.method !== 'GET') {
    res.setHeader('Allow', ['GET']);
    return res.status(405).json({ error: 'Method Not Allowed' });
  }

  try {
    const { slug } = req.query;

    if (!slug || !Array.isArray(slug)) {
      return res.status(400).json({ error: 'Invalid image path' });
    }

    const imageName = slug.join('/');
    const imageData = mockImageData[imageName as keyof typeof mockImageData];

    if (!imageData) {
      return res.status(404).json({ error: 'Image not found' });
    }

    // Set appropriate headers
    res.setHeader('Content-Type', imageData.contentType);
    res.setHeader('Cache-Control', 'public, max-age=31536000, immutable');
    res.setHeader('Content-Length', Buffer.from(imageData.data, 'base64').length.toString());

    // Send the image data
    const buffer = Buffer.from(imageData.data, 'base64');
    return res.status(200).send(buffer);

  } catch (error) {
    console.error('Mock image API error:', error);
    return res.status(500).json({ error: 'Internal server error' });
  }
}

// Disable body parsing for this API route
export const config = {
  api: {
    bodyParser: false,
  },
};